import { useEffect, useState } from "react";
import { useData } from "../../contexts/DataContext";
import { getMonth } from "../../helpers/Date";
import "./style.scss";

const Slider = () => {
  const { data } = useData();
  const [index, setIndex] = useState(0);

  // S'assurer que data?.focus est un tableau, sinon utiliser un tableau vide
  // Tri des événements par date en ordre décroissant
  const byDateDesc = [...(data?.focus || [])].sort((evtA, evtB) => new Date(evtB.date) - new Date(evtA.date));

  // Fonction pour passer à la carte suivante automatiquement
  const nextCard = () => {
    const nextIndex = index < byDateDesc.length - 1 ? index + 1 : 0;
    setTimeout(() => setIndex(nextIndex), 5000);
  };

  // Déclenche nextCard au montage du composant
  useEffect(() => {
    nextCard();
    // Nettoie le timeout lorsque le composant est démonté ou mis à jour
    return () => clearTimeout(nextCard);
  }, [byDateDesc]);

  return (
    <div className="SlideCardList">
      {byDateDesc.map((event, idx) => (
        <div
          key={event.id} // Utiliser un identifiant unique si possible
          className={`SlideCard ${index === idx ? "display" : "hide"}`}
        >
          <img src={event.cover} alt={event.title} />
          <div className="SlideCard__descriptionContainer">
            <div className="SlideCard__description">
              <h3>{event.title}</h3>
              <p>{event.description}</p>
              <div>{getMonth(new Date(event.date))}</div>
            </div>
          </div>
        </div>
      ))}
      <div className="SlideCard__paginationContainer">
        <div className="SlideCard__pagination">
          {byDateDesc.map((event, radioIdx) => (
            <input
              key={`radio-${event.id}-${radioIdx}`} // Clé unique pour chaque input radio
              type="radio"
              name="radio-button"
              checked={index === radioIdx}
              onChange={() => setIndex(radioIdx)}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Slider;
