import { useEffect, useState } from "react";
import { useData } from "../../contexts/DataContext";
import { getMonth } from "../../helpers/Date";

import "./style.scss";

const Slider = () => {
  const { data } = useData();
  const [index, setIndex] = useState(0);
  const byDateDesc = data?.focus.sort((evtA, evtB) =>
/* Inversement des valeurs dans la condition, initialement -1 : 1 pour obtenir l'ordre décroissant */
    new Date(evtA.date) < new Date(evtB.date) ? 1 : -1
  );

  // Ajout d'une constante pour initialiser l'indice dans notre tableau pour la partie focus
  const ImgFocus = data?.focus.length;

  // État pour indiquer si le slider est en pause
  const [paused, setPaused] = useState(false);

  const nextCard = () => {
    if (!paused) { // Vérifier si le slider n'est pas en pause
      setTimeout(() => setIndex(index < ImgFocus - 1 ? index + 1 : 0), 5000);
    }
  };

  useEffect(() => {
    nextCard();
  }, [index, paused]); // Effect dépendant de l'état index et paused

  // Gestionnaire d'événement pour la pression de la touche "Espace"
  const handleSpaceKeyDown = (event) => {
    if (event.code === "Space") {
      setPaused(true); // Mettre le slider en pause lorsque la touche "Espace" est enfoncée
    }
  };

  // Gestionnaire d'événement pour le relâchement de la touche "Espace"
  const handleSpaceKeyUp = (event) => {
    if (event.code === "Space") {
      setPaused(false); // Reprendre le slider lorsque la touche "Espace" est relâchée
    }
  };

  // Ajout des gestionnaires d'événements à l'écoute des événements de pression et de relâchement de la touche "Espace"
  useEffect(() => {
    window.addEventListener("keydown", handleSpaceKeyDown);
    window.addEventListener("keyup", handleSpaceKeyUp);
    return () => {
      window.removeEventListener("keydown", handleSpaceKeyDown);
      window.removeEventListener("keyup", handleSpaceKeyUp);
    };
  }, []); // Effect uniquement lors du montage du composant

  return (
    <div className="SlideCardList">
      {byDateDesc?.map((event, idx) => (
        <div
          key={event.title}
          className={`SlideCard SlideCard--${
            index === idx ? "display" : "hide"
          }`}
        >
          <img src={event.cover} alt="forum" />
          <div className="SlideCard__descriptionContainer">
            <div className="SlideCard__description">
              <h3>{event.title}</h3>
              <p>{event.description}</p>
              <div>{getMonth(new Date(event.date))}</div>
            </div>
          </div>
        </div>
      ))}

      <div className="SlideCard__paginationContainer">
        <div className="SlideCard__pagination">
          {/* Ajout de focus en paramètre pour créer des bullets en fonction de la clé focus dans notre tableau */}
          {byDateDesc?.map((focus, radioIdx) => (
            <input
              key={`${focus.title}`}
              type="radio"
              name="radio-button"
               /* Syntaxe modifiée idx en index */
              checked={index === radioIdx}
              /* Ajout de readOnly pour la gestion de l'état */
              readOnly
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Slider;
