import React, { useEffect, useState } from "react";
import { useData } from "../../contexts/DataContext";
import { getMonth } from "../../helpers/Date";

import "./style.scss";

const Slider = () => {
  const { data } = useData();
  const [index, setIndex] = useState(0);
  const [isPaused, setIsPaused] = useState(false); // État pour contrôler la pause du slider
  const byDateDesc = data?.focus.sort((evtA, evtB) =>
    new Date(evtA.date) < new Date(evtB.date) ? 1 : -1
  );

  const ImgFocus = data?.focus.length;

  const nextCard = () => {
    if (!isPaused) {
      setIndex((prevIndex) => (prevIndex < ImgFocus - 1 ? prevIndex + 1 : 0));
    }
  };

  useEffect(() => {
    const interval = setInterval(nextCard, 5000);

    // Nettoie l'intervalle lorsque le composant est démonté
    return () => clearInterval(interval);
  }, [isPaused]); // On change l'intervalle uniquement lorsque isPaused change

  // Gestion de la touche "espace" pour mettre en pause ou reprendre le slider
  const handleSpaceKeyPress = (event) => {
    if (event.keyCode === 32) {
      setIsPaused((prevPaused) => !prevPaused);
    }
  };

  useEffect(() => {
    window.addEventListener("keydown", handleSpaceKeyPress);

    // Nettoie l'écouteur d'événement lorsque le composant est démonté
    return () => window.removeEventListener("keydown", handleSpaceKeyPress);
  }, []);

  return (
    <div className="SlideCardList">
      {byDateDesc?.map((event, idx) => (
        <div
          key={event.title}
          className={`SlideCard SlideCard--${index === idx ? "display" : "hide"}`}
        >
          <img src={event.cover} alt="forum" />
          <div className="SlideCard__descriptionContainer">
            <div className="SlideCard__description">
              <h3>{event.title}</h3>
              <p>{event.description}</p>
              <div>{getMonth(new Date(event.date))}</div>
            </div>
          </div>
        </div>
      ))}

      <div className="SlideCard__paginationContainer">
        <div className="SlideCard__pagination">
          {byDateDesc?.map((focus, radioIdx) => (
            <input
              key={`${focus.title}`}
              type="radio"
              name="radio-button"
              checked={index === radioIdx}
              readOnly
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Slider;
