import { useEffect, useState } from "react";
import { useData } from "../../contexts/DataContext";
import { getMonth } from "../../helpers/Date";

import "./style.scss";

const Slider = () => {
  const { data } = useData();
  const [index, setIndex] = useState(0);
  const [isPaused, setIsPaused] = useState(false);  // Ajout d'un état pour gérer la pause

  // Triage des événements par date décroissante, initialement -1 : 1 pour obtenir l'ordre décroissant
  const byDateDesc = data?.focus.sort((evtA, evtB) =>
    new Date(evtA.date) < new Date(evtB.date) ? 1 : -1
  );

  // Ajout d'une constante pour initialiser l'indice dans notre tableau pour la partie focus
  const ImgFocus = data?.focus.length;

  const nextCard = () => {
    // Ajout de la condition de pause et ajustement de la taille du tableau à l'indice actuel
    if (!isPaused) {  // Vérification de l'état de pause
      setTimeout(() => setIndex(index < ImgFocus - 1 ? index + 1 : 0), 5000);
    }
  };

  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.key === ' ') {
        setIsPaused(!isPaused);  // Basculer l'état de pause lors de la pression de la touche espace
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    nextCard();

    return () => {
      window.removeEventListener('keydown', handleKeyDown);  // Nettoyage de l'écouteur d'événements
    };
  }, [index, isPaused]);  // Mise à jour des dépendances pour inclure isPaused

  return (
    <div className="SlideCardList">
      {byDateDesc?.map((event, idx) => (
        <div
          key={event.title}
          className={`SlideCard SlideCard--${index === idx ? "display" : "hide"}`}
        >
          <img src={event.cover} alt="forum" />
          <div className="SlideCard__descriptionContainer">
            <div className="SlideCard__description">
              <h3>{event.title}</h3>
              <p>{event.description}</p>
              <div>{getMonth(new Date(event.date))}</div>
            </div>
          </div>
        </div>
      ))}

      <div className="SlideCard__paginationContainer">
        <div className="SlideCard__pagination">
          {/* Ajout de focus en paramètre pour créer des bullets en fonction de la clé focus dans notre tableau */}
          {byDateDesc?.map((focus, radioIdx) => (
            <input
              key={`${focus.title}`}
              type="radio"
              name="radio-button"
              checked={index === radioIdx}
              readOnly  // Ajout de readOnly pour la gestion de l'état
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Slider;
